) { use bevy::input::mouse::MouseScrollUnit; for ev in evr_scroll.read() { match ev.unit { MouseScrollUnit::Line => { println!("Scroll (line units): vertical: {}, horizontal: {}", ev.y, ev.x); } MouseScrollUnit::Pixel => { println!("Scroll (pixel units): vertical: {}, horizontal: {}", ev.y, ev.x); } } }
