) { if kbd.just_pressed(KeyCode::F12) { let mut vis = q.single_mut(); *vis = match *vis { Visibility::Hidden => Visibility::Visible, _ => Visibility::Hidden, }; }
} app.add_systems(Startup, setup_fps_counter);
app.add_systems(Update, ( fps_text_update_system, fps_counter_showhide,
));

